export const Actions = {
    Notify: "NOTIFY",
    Auth: "AUTH",
    Cart: "CART",
}

export const addToCart = (data, id) => {
    if (data.inStock == 0)
        return { type: "notify", payload: { msg: { err: 'This product out of stock' } } }

}
