import React from 'react';

function Notify({ notify }) {
    return (
        <div className="fixed top-2 right-2 w-72">
            <div className={`shadow-md rounded-sm overflow-hidden mt-4 text-gray-700`} >
                <p className="p-2">{notify.msg.err && notify.msg.err}</p>
                <div className={`h-3 ${notify.msg.success ? `bg-green-300` : `bg-red-400`}`}></div>
            </div>
        </div>
    );
}

export default Notify;
