import React, { useContext, useEffect, useState } from 'react';
import { DataContext } from '../../store/GlobalState';

function Notify({ msg }) {
    const { state, dispatch } = useContext(DataContext)
    const [width, setWidth] = useState(0)
    const [intervalId, setIntervalId] = useState(0)
    const [exit, setExit] = useState(false)
    const handleStartTimer = () => {

        const id = setInterval(() => {
            setWidth(prev => {
                if (prev < 100) return prev + 0.5;

                clearInterval(id)
                return prev;
            })
        }, 5);

        setIntervalId(id)
    }


    const handleCloseNotif = () => {
        setExit(true)
        setTimeout(() => {
            dispatch({ type: "NOTIFY", payload: {} })
        }, 400);
    }

    useEffect(() => {
        handleStartTimer()
        return () => {
            handleCloseNotif()
        }
    }, [exit])

    return (
        <div onClick={handleCloseNotif} className=" fixed top-2 right-2 w-72">
            <div className={`notify shadow-md rounded-sm overflow-hidden mt-4 text-gray-700 ${exit ? 'exit' : ''}`} >
                <p className="px-2 py-4">{msg ? msg.err : msg.success}</p>
                <div className={`h-3 ${msg.success ? `bg-green-300` : `bg-red-400`}`} style={{ width: `${width}%` }} ></div>
            </div>
        </div >
    );
}

export default Notify;
