export const Actions = {
    Notify: "NOTIFY",
    Auth: "AUTH",
    Cart: "CART",
}

export const addToCart = (product, id) => {
    if (product.inStock == 0)
        return { type: "NOTIFY", payload: { msg: { err: 'This product out of stock' } } }

    const inCart = cart.every(item => item._id !== product._id)
    if (!inCart)
        return { type: "CART", payload: { msg: { err: 'This product out of stock' } } }

}
