import React from 'react';

function Input({ as, ...rest }) {
    switch (as) {
        case "text":
            return <InputText {...rest} />
        case "password":
            return <InputPassword {...rest} />
        case "email":
            return <InputEmail {...rest} />
        default:
            break;
    }
}

const InputText = ({ name, label, ...rest }) => {
    return (
        <div className="w-full">
            <label htmlFor={name} className="inline-block mb-4 text-sm" >{label}</label>
            <input type="text" name={name} id={name} className="block border w-full px-3 py-2" {...rest} />
        </div>
    );
}

const InputPassword = ({ name, label, ...rest }) => {
    return (
        <div className="w-full">
            <label htmlFor={name} className="inline-block mb-4 text-sm" >{label}</label>
            <input type="password" name={name} id={name} className="block border w-full px-3 py-2" {...rest} />
        </div>
    );
}

const InputEmail = ({ name, label, ...rest }) => {
    return (
        <div className="w-full">
            <label htmlFor={name} className="inline-block mb-4 text-sm" >{label}</label>
            <input type="email" name={name} id={name} className="block border w-full px-3 py-2" {...rest} />
        </div>
    );
}



export default Input;
