import React from 'react';
import MainApp from '../../components/layouts/MainApp';
import { getData } from '../../utils/fetchData';
import CardImage from '../../components/molecules/Card/CardImage';
import Image from '../../components/atoms/Image';

function DetailProduct({ product: { _id, images, title, inStock, price, description, content, sold } }) {
    return (
        <MainApp>
            <div className="px-3 md:px-8 pb-8" >
                <div className="pt-5 mb-5">
                    <h2 className="text-xl font-bold text-true-gray-500">Product</h2>
                </div>
                <div className="flex gap-4 space-y-4" >
                    <div className="md:w-7/12 ">
                        <div className="p-2 border rounded-t border-gray-100 shadow-sm" >
                            <CardImage src={images[0].url} className="object-cover h-96" />
                            <div className="flex gap-1 pt-2" >
                                {
                                    images.map((image, index) => <Image key={index} src={image.url} className="w-20 h-20 object-cover object-top border-2 border-transparent hover:border-blue-300" />)
                                }
                            </div>
                        </div>
                    </div>
                    <div className={`md:w-4/12`} >
                        <h2 className="text-2xl font-bold text-true-gray-800 capitalize " >{title}</h2>
                        <div></div>
                        <div className={`flex justify-between w-full my-4 items-center`} >
                            <div className={`text-yellow-300 font-sm`} >Sold: {sold}</div>
                            <div className={`text-green-400 font-sm`}>Stock: {inStock}</div>
                        </div>
                        <div>
                            <h4 className={`font-bold border-b inline-block`}>Description</h4>
                            <div>
                                {description}
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        </MainApp>
    );
}

export default DetailProduct;

export async function getServerSideProps({ params: { id: id } }) {
    const product = await getData(`product/${id}`)
    if (product.err) {
        return {
            notFound: true
        }
    }
    return { props: { product } }
}
