import React from 'react';
import MainApp from '../../components/layouts/MainApp';
import { getData } from '../../utils/fetchData';
import CardImage from '../../components/molecules/Card/CardImage';

function DetailProduct({ product: { _id, images, title, inStock, price, description, content } }) {
    return (
        <MainApp>
            <div className="px-3 md:px-8 pb-8" >
                <div className="pt-5 mb-5">
                    <h2 className="text-xl font-bold text-true-gray-500">Product</h2>
                </div>
                <div className="" >
                    <div className="md:w-1/2">
                        <div>

                            <CardImage src={images[0].url} />
                        </div>
                    </div>
                </div>
            </div>
        </MainApp>
    );
}

export default DetailProduct;

export async function getServerSideProps({ params: { id: id } }) {
    const product = await getData(`product/${id}`)
    if (product.err) {
        return {
            notFound: true
        }
    }
    return { props: { product } }
}
