import React, { useContext, useState, useEffect } from 'react';
import Content from '../components/container/Content';
import MainApp from '../components/layouts/MainApp';
import Fields from '../components/molecules/Fields';
import { DataContext } from '../store/GlobalState';
import valid from '../utils/validate'

function profile() {
    const init = {
        avatar: '',
        name: '',
        email: '',
        password: '',
        password2: ''
    }
    const [data, setData] = useState(init)
    const { avatar, name, email, password, password2 } = data

    const { state: { auth }, dispatch } = useContext(DataContext)

    useEffect(() => {
        if (auth.user)
            setData({ ...data, name: auth.user.name })
    }, [auth.user]);

    const onChange = (e) => {
        const { name, value } = e.target
        setData({ ...data, [name]: value })
        dispatch({ type: "NOTIFY", payload: {} })
    }

    const onUpdate = (e) => {
        e.preventDefault()
        if (password) {

        }
    }

    if (!auth.user) return null
    return (
        <MainApp>
            <Content title="Profile" >
                <div className={`space-y-4 md:w-full lg:max-w-xl`} >
                    <div>
                        <h1>{auth.user.role == "user" ? "User Profile" : "Admin Profile"}</h1>
                    </div>
                    <div className={`w-32 h-32 bg-green-300 text-center mx-auto rounded-full`} >
                        <img src={auth.user.avatar} className={`rounded-full w-full h-full object-cover `} />
                    </div>
                    <Fields type="text" name="name" label="Name" value={name} onChange={onChange} />
                    <Fields type="email" name="email" label="Email" value={auth.user.email} onChange={onChange} />
                    <Fields type="text" name="password" label="New Password" onChange={onChange} />
                    <Fields type="text" name="password2" label="Confirm New Password" onChange={onChange} />
                    <button onClick={onUpdate} className={`px-6 py-2.5 bg-green-300 focus:outline-none text-white font-bold`} >Update</button>
                </div>
            </Content>
        </MainApp>
    );
}

export default profile;
