import Button from "../../atoms/Button";

export function BtnPrimary({ children, block = false }) {
    return (
        <Button className={`px-4 py-2 bg-blue-300 text-white font-bold ${block && 'w-full' || ''}`} >{children}</Button>
    );
}

export function BtnSecondary({ children, block = false }) {
    return (
        <div className={`bg-white-300 text-blue-300 border-2 inline-block box-border border-blue-200 font-bold ${block && 'w-full' || ''}`} >
            <Button className="px-4 py-2"  >{children}</Button>
        </div>
    );
}
