import React, { useContext, useState, useEffect } from 'react';
import Content from '../components/container/Content';
import MainApp from '../components/layouts/MainApp';
import Fields from '../components/molecules/Fields';
import { DataContext } from '../store/GlobalState';

function profile() {
    const init = {
        avatar: '',
        name: '',
        password: '',
        password2: ''
    }
    const [data, setData] = useState(init)
    const { avatar, name, password, password2 } = data

    const { state: { auth }, dispatch } = useContext(DataContext)

    useEffect(() => {

    }, []);

    if (!auth.user) return null
    return (
        <MainApp>
            <Content title="Profile" >
                <div className={`space-y-4 md:w-full lg:max-w-xl`} >
                    <div>
                        <h1>{auth.user.role == "user" ? "User Profile" : "Admin Profile"}</h1>
                    </div>
                    <div className={`w-32 h-32 bg-green-300 text-center mx-auto rounded-full`} >
                        <img src={auth.user.avatar} className={`rounded-full w-full h-full object-cover `} />
                    </div>
                    <Fields type="text" name="name" label="Name" />
                    <Fields type="text" name="password" label="Password" />
                    <Fields type="text" name="password2" label="Confirm Password" />
                    <button className={`px-6 py-2.5 bg-green-300 focus:outline-none text-white font-bold`} >Update</button>
                </div>
            </Content>
        </MainApp>
    );
}

export default profile;
