import React, { useState, useEffect, useContext } from 'react';
import MainApp from '../../components/layouts/MainApp';
import Content from '../../components/container/Content';
import { DataContext } from '../../store/GlobalState';
import { useRouter } from 'next/router';


function DetailOrder() {
    const { state, dispatch } = useContext(DataContext)
    const { order, auth } = state
    console.log(order.cart.map(item => item));
    const router = useRouter()

    const [orderDetail, setOrderDetail] = useState([])

    useEffect(() => {
        const newArr = order.filter(item => item._id === router.query.id)
        console.log(newArr);
        setOrderDetail(newArr)
    }, [order]);


    return (
        <MainApp>
            <Content title="Detail Order">

                <div>
                    <button className={`bg-blue-400 px-4 py-1 text-white`} >Go back</button>
                </div>
                <div className={`max-w-xl my-5`} >
                    <div>
                        <h2>Shipping</h2>
                    </div>
                    {
                        orderDetail.map(order => (
                            <div key={order._id} >
                                <p>Name : {order.user.name}</p>
                                <p>Email : {order.user.email}</p>
                                <p>Address : {order.address}</p>
                                <p>Mobile : {order.mobile}</p>
                            </div>

                        ))
                    }

                    <div>
                        <h2>Order items</h2>
                        {/* {
                            order.cart.map(item => (
                                <div>
                                    <img src={item.images[0].url} alt={item.images[0].url} className={`w-20 h-11`} />
                                </div>

                            ))
                        } */}
                    </div>

                </div>
            </Content>

        </MainApp>
    );
}

export default DetailOrder;
