import React, { useEffect, useState } from 'react';

function Notify({ msg }) {
    const [width, setwidth] = useState(0)

    useEffect(() => {
        const id = setInterval((width) => {
            if (width < 100) width + 0.5
        }, 20);
    }, [width])

    return (
        <div className=" fixed top-2 right-2 w-72">
            <div className={`notify shadow-md rounded-sm overflow-hidden mt-4 text-gray-700`} >
                <p className="px-2 py-4">{msg ? msg.err : msg.success}</p>
                <div className={`h-3 ${msg.success ? `bg-green-300` : `bg-red-400`}`}></div>
            </div>
        </div>
    );
}

export default Notify;
