import React from 'react';

function Fields({ type, ...rest }) {
    switch (type) {
        case "text":
        case "password":
        case "email":
            return <Input {...rest} />
        default:
            break;
    }
}

const Input = ({ name, label, type, ...rest }) => {
    console.log(type);
    return (
        <div className="w-full">
            <label htmlFor={name} className="inline-block mb-1.5 text-lg capitalize text-gray-800" >{label}</label>
            <input type={type} name={name} id={name} className="block border w-full px-3 py-2 rounded-sm focus:outline-none focus:ring-1 ring-blue-400" {...rest} />
        </div>
    );
}


export default Fields;
